mapper.select:
SELECT NVL(SUM(PPRICE*AMOUNT), 0) ORDERPRICE
FROM CART
WHERE PNO = #{ pno }
  AND CARTUSERID = #{ cartuserid }

mapper.update:
UPDATE TABLE CART
SET AMOUNT = #{ amount }
WHERE CARTUSERID = #{ cartuserid }
AND CARTNO = #{ cartno }

